import React, { Component } from 'react';

import { Link } from "react-router-dom";


class Commercant extends React.Component {
  constructor(props) {
    super(props);
    this.state = { mail: '',
                    utilisateur: '',
                    avis : '' };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.mail + this.state.utilisateur + this.state.avis);
    event.preventDefault();
  }

  render() {
    return (
      <div>
       
       <form onSubmit={this.handleSubmit}>
          
          
        
          <div className="form-group text-center">
          <input type="text" id="formulaire" className="form-control form-control-sm" placeholder="Adresse email"  autoComplete="on" required minLength={2} value={this.state.mail} onChange={this.handleChange}/>
         <input type="password" className="form-control form-control-sm" name="motdepasse" placeholder="Mot de passe" style={{ padding: '3px 8px 4px -6px', borderWidth: '2px', margin: '0px', marginTop: '10px', marginBottom: '9px', borderRadius: '7px', boxShadow: '0px 0px var(--indigo)' }} autoComplete="on" required minLength={2} />
         <button type="submit" class="btn btn-outline-primary btn-rounded" data-mdb-ripple-color="dark">
                  Laisser un avis
                </button>
          </div>
        </form>
          
        
      </div>
    );
  }
}
export default Commercant;
