import React, { Component } from 'react';
import "./Admin.css";
import logo from './ece.png';
import styled, { keyframes } from 'styled-components';

import { fadeIn } from 'react-animations';


const bounceAnimation = keyframes`${fadeIn}`;

const BouncyDiv = styled.div`
  animation: 1s ${bounceAnimation};
`;


class Admin extends Component {
    constructor(props) {
        super(props);
        this.state = {
            products: [],
            category: this.props.match.params.categoryId
        };

    }
    componentDidMount() {
        fetch(`https://fr.openfoodfacts.org/categorie/${this.props.name}.json`)
            // fetch(`https://world.openfoodfacts.org/api/v0/product/${this.props.name}.json`)
            .then((response) => {
                return response.json()

            })

            .then((result) => {
                this.setState(result);
                console.log(result);

            })

    }



    
    render() {
        let categoryId = this.props.match.params.categoryId;


        return (
            <BouncyDiv>
                <div className="card mb-5">
                    {categoryId}
                    <div className="card-body">
                        <img src={logo} alt="Ece" id="img" />
                        <p id="para">Caract√©ristiques</p>
                        
                    </div>
                </div>
            </BouncyDiv>
        );


    }
}

export default Admin;
