import React, { Component } from 'react';
import "./NbreCas.css"
import styled, { keyframes } from 'styled-components';
import { fadeIn } from 'react-animations';




class NbreCas extends Component {

    constructor(props) {
        super(props);
        this.state = {
            products: []
        };
    }
    componentDidMount() {
        fetch(`https://world.openfoodfacts.org/category/${this.props.name}.json`)
            // fetch(`https://world.openfoodfacts.org/api/v0/product/${this.props.name}.json`)
            .then((response) => {
                return response.json()

            })

            .then((result) => {
                this.setState(result);
                console.log(result);

            })

    }
    componentDidUpdate(prevProps) {

        if (this.props.name !== prevProps.name) {
            fetch(`https://world.openfoodfacts.org/category/${this.props.name}.json`)
                // fetch(`https://world.openfoodfacts.org/api/v0/product/${this.props.name}.json`)
                .then((response) => {
                    this.setState({
                        products: [
                            {
                                image_url: this.state.products.image_url

                            }
                        ]

                    })
                    return response.json()

                })

                .then((result) => {
                    this.setState(result);
                    console.log(result);
                })
        }
    }

    render() {

        return (
            <div id="testttt">
                {
                    this.state.products.map(empresa => {
                        return (
                            <div className="row mb-5">
                                <div className="col-xl-4 col-lg-6 mb-5">
                                    <div id="card">
                                        <div className="card-body">
                                            <img class="fit-picture" src={empresa.image_url} alt="Grapefruit slice atop a pile of other slices" />
                                            <h6 className="card-title mt-2">
                                                {/*this.state.products[0].generic_name_en*/}
                                            </h6>



                                        </div>
                                    </div>
                                </div>
                            </div>
                        )
                    })
                }


            </div>

        );
    }
}


export default NbreCas;
