import React, { Component } from 'react';

import { Link } from "react-router-dom";


class Commercant extends React.Component {
  constructor(props) {
    super(props);
    this.state = { mail: '',
                    utilisateur: '',
                    avis : '' };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.mail + this.state.utilisateur + this.state.avis);
    event.preventDefault();
  }

  render() {
    return (
      <div>
       
          <form onSubmit={this.handleSubmit}>
          
          
        
          <button type="button" class="btn btn-outline-primary btn-rounded" data-mdb-ripple-color="dark">
                  Laisser un avis
                </button>
                <input type="text" id="formulaire" className="form-control form-control-sm"  placeholder="Adresse mail"  autoComplete="on" required minLength={2}  onChange={this.handleChange} />
                <input type="text" id="formulaire" className="form-control form-control-sm" name="nom" placeholder="Nom d'utilisateur" value={this.state.utilisateur} onChange={this.handleChange} />
                <textarea id="form-control"  name="Descripition de votre commerce" placeholder="Descripition de votre commerce" rows={9} defaultValue={""} value={this.state.avis} onChange={this.handleChange}/>
          </form>
          
        
      </div>
    );
  }
}
export default Commercant;
