import React, { Component } from 'react';

import { Link } from "react-router-dom";


class Commercant extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      avis : '',
      nom : ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({
        [event.target.name]: event.target.value
    });
}

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.avis + this.state.nom);
    event.preventDefault();
  }

  render() {
    return (
      <div>
       
       <form onSubmit={this.handleSubmit}>
          
          
        
          <div className="form-group text-center">
          <input type="text" value={this.state.avis} onChange={this.handleChange} />
          <input type="text" value={this.state.nom} onChange={this.handleChange} />
         <button type="submit" class="btn btn-outline-primary btn-rounded" data-mdb-ripple-color="dark">
                  Laisser un avis
          </button>
          </div>
        </form>
          
        
      </div>
    );
  }
}
export default Commercant;
