import React, { Component } from 'react';
import "./NbreCas.css"
import styled, { keyframes } from 'styled-components';
import { fadeIn } from 'react-animations';




class NbreCas extends Component {

    constructor(props) {
        super(props);
        this.state = {
            product: {
                image_thumb_url: ""
            }
        };
    }
    componentDidMount() {
        fetch(`https://world.openfoodfacts.org/api/v0/product/${this.props.name}.json`)
            .then((response) => {
                return response.json()
                
            })

            .then((result) => {
                this.setState(result);
                  console.log(result);
                 
            })
            
    }
    componentDidUpdate(prevProps) {

       if (this.props.name !== prevProps.name){
        fetch(`https://world.openfoodfacts.org/api/v0/product/${this.props.name}.json`)
        .then((response) => {
            this.setState({
                product: {
                    image_thumb_url: json.artObjects[0].links.web
                }
                //data: json.artObjects[0].links.web
            });
            return response.json()
            
        })

        .then((result) => {
            this.setState(result);
              console.log(result);
        })
       }
    }

    render() {
        
       console.log(this.props.name);

        return (
           
            <div id="card">
               <img class="fit-picture"
     src={this.state.product.image_thumb_url}
     alt="Produit"/>
            </div>
            
        );
    }
}


export default NbreCas;
