import React, { Component } from 'react';
import "./NbreCas.css"
import styled, { keyframes } from 'styled-components';
import { fadeIn } from 'react-animations';




class NbreCas extends Component {

    constructor(props) {
        super(props);
        this.state = {
            product: {
                image_small_url: ""
            }
        };
    }
    componentDidMount() {
        fetch(`https://fr.openfoodfacts.org/categorie/bio.json`)
        // fetch(`https://world.openfoodfacts.org/api/v0/product/${this.props.name}.json`)
            .then((response) => {
                return response.json()
                
            })

            .then((result) => {
                this.setState(result);
                  console.log(result);
                 
            })
            
    }
    componentDidUpdate(prevProps) {

       if (this.props.name !== prevProps.name){
        fetch(`https://fr.openfoodfacts.org/categorie/bio.json`)
        // fetch(`https://world.openfoodfacts.org/api/v0/product/${this.props.name}.json`)
        .then((response) => {
            this.setState({
                product:{
                    image_small_url:this.state.product.image_small_url
                }
            })
            return response.json()
            
        })

        .then((result) => {
            this.setState(result);
              console.log(result);
        })
       }
    }

    render() {
       console.log(this.props.name);
       console.log(this.state.product.image_thumb_url);

        return (
           
            <div id="card">
                <div className="card-body">
                <img class="fit-picture" src={this.state.product.image_small_url} alt="Grapefruit slice atop a pile of other slices"/>
                <h6 className="card-title mt-2">
                {this.state.product.generic_name_en}
                </h6>


                    
                </div>

              
            </div>
            
        );
    }
}


export default NbreCas;
